import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Movie from './Movie';
import './Suggested.css';

const Suggested = () => {
    const [movies, setMovies] = useState([]);

    async function getMovies() {
        const { data } = await axios.get('https://www.omdbapi.com/?s=movie&y=2023&apikey=2121abfd');

        const filteredMovies = [];

        const loadPromises = data.Search.map(movie => new Promise((resolve) => {
            const img = new Image();
            img.onload = function () {
                if (this.height > 300) {
                    filteredMovies.push(movie);
                }
                resolve();
            };
            img.src = movie.Poster;
        }));

        await Promise.all(loadPromises);

        setMovies(filteredMovies.slice(0, 16));
    }

    useEffect(() => {
        getMovies();
    }, []);

    return (
        <div className="suggested">
            <p className='suggested__header'>Latest releases</p>
            <div className="suggested__movies">
                {movies.map((movie, index) => <Movie movie={movie} key={index} />)}
            </div>
        </div>
    );
}

export default Suggested;
