import { Search, Theaters } from '@mui/icons-material';
import { Avatar } from '@mui/material';
import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Navigation.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSpinner } from '@fortawesome/free-solid-svg-icons';

const Navigation = ({ onSearch }) => {
    let navigate = useNavigate();
    const [loading, setLoading] = useState();
    const [inputValue, setInputValue] = useState('');

    async function handleSubmit(event) {
        setLoading(true);
        event.preventDefault();

        const { data } = await axios.get(`https://www.omdbapi.com/?s=${inputValue}&apikey=2121abfd`);

        const filteredMovies = [];

        const loadPromises = data.Search.map(movie => new Promise((resolve) => {
            const img = new Image();
            img.src = movie.Poster;
            img.onload = function () {
                if (this.height > 330) {
                    filteredMovies.push(movie);
                }
                resolve();
            };
        }));

        await Promise.all(loadPromises);

        onSearch(filteredMovies, inputValue);

        console.log(filteredMovies);

        setLoading(false);
        navigate('/search');
    }
    
    function handleChange(event) {
        setInputValue(event.target.value);
    }

    return (
        <nav className='navbar'>
            <div className="navbar__logo" onClick={() => navigate('/')}>
                <Theaters />

                <h1 className='navbar__logoName'>REEL
                    <span style={{ color: '#000' }}>SPOT</span>
                </h1>
            </div>

            <div className='navbar--rightSection'>
                <form onSubmit={handleSubmit} className='navbar__searchBar'>
                    <input
                        className='navbar__searchBar--input'
                        type="text"
                        placeholder="Search by title"
                        value={inputValue}
                        onChange={handleChange}
                        onKeyDown={(event) => event.key === "Enter" && handleSubmit(event)}
                    />
                    <button
                        className="navbar__searchBar--btn"
                        type="submit"
                    >
                        {loading ? <FontAwesomeIcon className='navbar__searchBar--spinner' icon={faSpinner} /> : <Search />}
                    </button>
                </form>

                <div className="navbar__profile">
                    <Avatar style={{width: 34, height: 34}} />
                </div>
            </div>

        </nav>
    );
}

export default Navigation;
