import axios from 'axios';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Movie.css';

const Movie = ({ movie }) => {
    const [rating, setsMovies] = useState("");

    async function getMovies() {
        const { data } = await axios.get(`https://www.omdbapi.com/?i=${movie.imdbID}&apikey=2121abfd`);
        setsMovies(data.Search);
    }

    useEffect(() => {
        getMovies();
    }, []);
    
    
    return (
        <div className="movie" key={movie.imdbID}>
            <figure className="movie__poster--wrapper">
                <img className="movie__poster" src={movie.Poster} />
            </figure>
            <div className="movie__description">
                <h1 className="movie__title">{movie.Title}</h1>
                <div className="movie__info--wrapper">
                    <h2 className="movie__type">
                        {movie.Type}
                        <span className="movie__year"> {movie.Year}</span>
                    </h2>
                    <Link to={`https://www.imdb.com/title/${movie.imdbID}`} className="movie__link" target='_blank'>
                        IMDb
                    </Link>
                    {
                        
                    }
                </div>
            </div>
        </div>
    );
}

export default Movie;
